svg.script:
[
  'pathElement1',
  'pathElement2',
  'rectElement1',
  'rectElement2',
  'polygonElement1',
  'polygonElement2',
  'polylineElement1',
  'polylineElement2'
].forEach(elementId => {
  test(function() {
    let element = document.getElementById(elementId);
    var length = element.getTotalLength();
    assert_approx_equals(length, 200, 1e-5);
  }, document.title + ', ' +
     document.getElementById(elementId).getAttribute('description'));
});

[
  'circleElement1',
  'circleElement2',
  'ellipseElement1',
  'ellipseElement2'
].forEach(elementId => {
  test(function() {
    let element = document.getElementById(elementId);
    var length = element.getTotalLength(0);
    assert_approx_equals(length, 313.6517333984375, 1e-5);
  }, document.title + ', ' +
     document.getElementById(elementId).getAttribute('description'));
});
